#include <iostream>#include <math.h>#include <algorithm>using namespace std;int main() {	int n;	cin >> n;	while (n--) {		int x1, y1, r1, x2, y2, r2;		cin >> x1 >> y1 >> r1 >> x2 >> y2 >> r2;				int l = max(r1, r2);		int s = min(r1, r2);		int sub = l - s;		int sum = l + s;		double distance = sqrt(pow(x2 - x1, 2) + pow(y2 - y1, 2)); 		if (distance == 0) {			if (r1 == r2) {				cout << "-1" << endl;			}			else if (r1 != r2) {				cout << "0" << endl;			}		}		else if (sub >distance||distance > sum) {		cout << "0" << endl;		}		else if (sub <distance && distance < sum) {			cout << "2"<<endl;		}		else if (distance == sum ||distance== sub) {			cout << "1" << endl;		}	}}