#include <stdio.h>int dp[41];int fibo(int x) {	if (x == 0) {		return 0;	}	else if (x == 1) {		return 1;	}	if (dp[x] != 0) {		return dp[x];	}	else {		return dp[x] = fibo(x - 1) + fibo(x - 2);	}}int main() {		int t;	scanf("%d", &t);	int n;	for (int i = 0; i < t; i++) {		dp[0] = 0;		dp[1] = 1;		scanf("%d", &n);		if (n == 0) {			printf("%d %d\n", 1, 0);		}		else if (n == 1) {			printf("%d %d\n", 0, 1);		}		else {			fibo(n);			printf("%d %d\n", dp[n - 1], dp[n]);		}	}}