#include <stdio.h>//런타임에러?int arr[1000001];int main() {		long long min1, max1;	long long cnt= 0;	scanf("%lld %lld", &min1, &max1);		for (long long i = 2; i*i <= max1; i++) {		for (long long j = ((min1 - 1) / (i*i) + 1)*i*i; j <= max1;j+=i*i) {			arr[j-min1] = 1;		}	}	for (long long i = 0; i <= max1-min1+1; i++) {		if (arr[i]) cnt++;	}	printf("%lld", max1-min1 - cnt+1);	return 0;}