#include <iostream>#include <vector>#include <algorithm>using namespace std;int arr[5];int gcd(int x,int y) {	int z;	if (x < y) swap(x, y);	z = x%y;	if (z == 0) return y;	else gcd(y, z);}int main() {	for (int i = 0; i < 5; i++) {		cin >> arr[i];	}//	cout << gcd(12, 32);	vector<int> vec;	for (int i = 0; i < 3; i++) {		for (int j = i + 1; j < 4; j++) {			for (int k = j + 1; k < 5; k++) {				int tmp = arr[i] * arr[j] / gcd(arr[i], arr[j]); //arr[i], arr[j] 최소공배수				vec.push_back(tmp*arr[k] / gcd(tmp, arr[k])); //i, j, k 최소공배수			}		}	}	sort(vec.begin(), vec.end());	cout << vec[0];}