#include <iostream>#include <string>using namespace std;int cnt[100] = { 0, };int main() {	string a;	getline(cin, a);	//대문자: 65~90, 소문자: 97~122	for (int i = 0; i < a.length(); i++) {		if (97 <= a[i] && a[i] <= 122) {			cnt[a[i] - 32]++;		} //대문자만 ++		else cnt[a[i]]++;	}	int comp = 0;	int t;	bool result = true;	for (int i = 'A'; i <= 'Z'; i++) {		if (comp < cnt[i]) {			comp = cnt[i];			t = i;			result = true;		}		else if (comp == cnt[i]) {			result = false;		}	}	if (result == false) cout << '?';	else printf("%c", t);}