#include <iostream>#include <vector>using namespace std;int main() {	vector<int> vec;	int tmp;	cin>>tmp;	while(tmp>0){		vec.push_back(tmp%10);		tmp/=10;	}	// for(int i=0;i<vec.size();i++){	// cout<<vec[i]<<endl;	// }			int comp1,comp2;	bool result=false;	    if(vec.size()>1){	for(int i=0;i<vec.size();i++){		comp1=1;		comp2=1;		for(int j=0;j<=i;j++){			comp1*=vec[j];		}		for(int k=i+1;k<vec.size();k++){			comp2*=vec[k];		}		if(comp1==comp2){			result=true;			break;		}	}    }	if(result){		cout<<"YES";	}	else cout<<"NO";	}