#include <iostream>#include <vector>#include <list>using namespace std;int main() {	string str;	cin >> str;	list<char> a;	for (int i = 0; i < str.length(); i++) {		a.push_back(str[i]);	}	list<char>::iterator c = a.end(); //커서		int n;	cin >> n;	char tmp;	while (n--) {		cin >> tmp;		if (tmp == 'L') {			if (c != a.begin()) {				c--;			}		}		else if (tmp == 'D') {			if (c != a.end()) {				c++;			}		}		else if (tmp == 'B') {			if (c != a.begin()) {				list<char>::iterator d = c;				c--;				a.erase(c);				c = d;			}		}		else if (tmp == 'P') {			char tmp2;			cin >> tmp2;			a.insert(c,tmp2);		}	}	for (c=a.begin(); c != a.end(); c++) {		cout << *c;	}}