#include <iostream>#include <algorithm>#include <string>#include <functional> using namespace std;int main() {	int a;	string n;	cin >> n;	int size = n.size();	int t = stoi(n);	int* arr = new int[size];	for (int i = 0; i < size; i++) {		a=t % 10;		arr[i] = a;		t /= 10;	}	sort(arr, arr + size,greater<int>());	for (int i = 0; i < size; i++) {		cout << arr[i];	}}