#include <iostream>using namespace std;int main() {	int n, m = 0;	double sum = 0;	cin >> n;	int *arr = new int[n];	for (int i = 0; i < n; i++)		cin >> arr[i];	for (int j = 0; j < n; j++)		if (m <= arr[j])			m = arr[j];	for (int k = 0; k < n; k++) 		sum += (((double)arr[k] / m) * 100);		cout << fixed;	cout.precision(2);	cout << sum / n << endl;	return 0;}