#include <iostream>#include <algorithm>#include <vector>using namespace std;vector<long long> vec;int main() {	long long k, n; //랜선 수 k, 필요한 랜선 개수 n	cin >> k >> n;	long long tmp;	long long low = 1, high = 0;	for (int i = 0; i < k; i++) {		cin >> tmp;		if (tmp > high)high = tmp;		vec.push_back(tmp);	}	long long mid = 0; //이분탐색 중간점	long long ans; 	long long result = 0; 	while (low <= high) {		ans = 0;		mid = (low + high) / 2;		for (int i = 0; i < k; i++) {			ans += vec[i] / mid;		}		if (ans >= n) {			if (result < mid) {				result = mid;			}			low = mid + 1;		}		else {			high = mid - 1;		}	}	cout << result;}