#include <iostream>#include <queue>using namespace std;priority_queue<int, vector<int>, less<int>>maxheap;priority_queue<int, vector<int>, greater<int>>minheap;int main() {	int n;	scanf("%d", &n);	int t;	for (int i = 0; i < n; i++) {		scanf("%d", &t);		if (maxheap.size() == 0) {			maxheap.push(t);		}		else {			if (maxheap.size() == minheap.size()) {				maxheap.push(t);			}			else {				minheap.push(t);			}			if (maxheap.top() > minheap.top()) {				int maxT = maxheap.top();				int minT = minheap.top();				maxheap.pop();				minheap.pop();				maxheap.push(minT);				minheap.push(maxT);			}		}		printf("%d\n", maxheap.top());	}}