#include <iostream>#include <queue>using namespace std;int n, k;int check[100001];int bfs() {	queue<int> q;	q.push(n);	check[n] = 1;	while (!q.empty()) {		int next = q.front();		q.pop();		if (next == k) return check[next];		if (0 <= next - 1 && next - 1 <= 100000 && check[next - 1] == 0) {			check[next - 1] = check[next] + 1;			q.push(next - 1);		}		if (0 <= next + 1 && next + 1 <= 100000 && check[next + 1] == 0) {			check[next + 1] = check[next] + 1;			q.push(next + 1);		}		if (0 <= next * 1 && next * 2 <= 100000 && check[next * 2] == 0) {			check[next * 2] = check[next] + 1;			q.push(next * 2);		}	}}int main() {	cin >> n >> k;	cout << bfs()-1;}