#include <cstdio>#include <cstdlib>#include <cmath>#include <string.h>#include <queue>#include <iostream> #define ll long longusing namespace std; int main() {    ll n;    scanf("%lld", &n);        queue<bool> q;    while (n) {        q.push(n % 2);        n /= 2;    }     ll ans = 0;    ll add = 1;     while (!q.empty()) {        ans += q.front() * add;        add *= 3;        q.pop();    }     printf("%lld", ans);        return 0;} 