#include <iostream>#include <vector>#include <math.h>#include <algorithm>using namespace std;vector<int> vec;int sum[100000];int main() {	int n,tmp;	cin >> n;	for (int i = 0; i < n; i++) {		cin >> tmp;		vec.push_back(tmp);	}	sum[0] = vec[0];	int result=-1000;	for (int i = 1; i < n; i++) {		sum[i] = max(sum[i - 1] + vec[i], vec[i]);		result = max(result,sum[i]);	}	result = max(result, sum[0]);	cout << result;}