#include <iostream>#include <vector>#include <algorithm>using namespace std;vector<pair<int, int>> vec;int comp(pair<int,int>a, pair<int, int>b) {	if (a.second < b.second) return true;	else if (a.second == b.second) return a.first < b.first;	else return false;}int main() {	int n;	cin >> n;	int a, b;		for (int i = 0; i < n; i++) {		cin >> a >> b;		vec.push_back({ a,b });	}	sort(vec.begin(), vec.end(),comp);  	int t = 0;	int cnt = 0;	for (int i = 1; i < vec.size(); i++) {		if (vec[t].second <= vec[i].first) {			t = i;			cnt++;		}	}	cout << cnt+1;}