#include <iostream>#include <vector>#include <algorithm>using namespace std;int arr[501][501];int dp[501][501];int main() {	int n;	cin >> n;	for (int i = 0; i < n; i++) {		for (int j = 0; j <= i; j++) {			cin >> arr[i][j];		}	}	dp[0][0] = arr[0][0];	int result = 0;	for (int i = 1; i < n; i++) {		for (int j = 0; j <= i; j++) {			if (j == 0) { //왼쪽				dp[i][j] = dp[i - 1][j] + arr[i][j];			}			else if (j == i) { //오른쪽				dp[i][j] = dp[i - 1][j - 1] + arr[i][j];			}			else { //중간들				dp[i][j] = max(dp[i - 1][j - 1], dp[i - 1][j]) + arr[i][j];			}			if (result < dp[i][j]) {				result = dp[i][j];			}		}	}	cout << result;	}