#include <iostream>#include <vector>using namespace std;int main() {	int t, n, m, tmp;	cin >> t;	while (t--) {		vector<pair<int, int>> q;		pair<int, int> comp;		int cnt = 0;		cin >> n >> m;		for (int i = 0; i < n; i++) {			cin >> tmp;			q.push_back({ tmp,i });		}		while (1) {			bool tt = false;			int max = -1;			long sz = q.size();			for (int i = 0; i < sz; i++) {				if (q[0].first < q[i].first) {					if (max < q[i].first) {						max = q[i].first;					}					tt = true;				}			}			if (!tt && m == q[0].second) break;			if (max == -1) {				q.erase(q.begin() + 0);				cnt++;			}			else {				if (tt) {					while (q[0].first != max) {						comp = q[0];						q.erase(q.begin() + 0);						q.push_back(comp);					}					if (m == q[0].second) {						break;					}					q.erase(q.begin() + 0);					cnt++;				}				else if (!tt) {					while (q[0].second != m) {						q.erase(q.begin() + 0);						cnt++;					}				}			}		}		cout << cnt + 1 << endl;	}}