#include <iostream>#include <vector>using namespace std;int main() {	int n;	int cnt = 0;	cin >> n;	vector<int>vec;	vector<bool>check;	int tmp;	for (int i = 0; i < n; i++) {		cin >> tmp;		vec.push_back(tmp);		check.push_back(true); //다 소수다!	}	for (int i = 0; i < n; i++) {		if (vec[i] == 1) {			check[i] = false; //1은 소수가 아님		}		else if (vec[i] == 2) {			check[i] = true; //2는 소수임		}		else {			for (int j = 2; j < vec[i]; j++) {				if (vec[i] % j == 0) { //소수가 아니면 !					check[i] = false;				}			}		}	}	for (int i = 0; i < check.size(); i++) {		if (check[i] == true) {			cnt++;		}	}	cout << cnt;}