#include <iostream>using namespace std;typedef struct _node {	char left;	char right;	char data;}Node;Node nodes[127];void preorder(int x) {	cout << char(x+'A');	if (nodes[x].left >0) preorder(nodes[x].left);	if (nodes[x].right >0) preorder(nodes[x].right);}void inorder(int x) {	if (nodes[x].left >0) inorder(nodes[x].left);	cout << char(x + 'A');	if (nodes[x].right >0) inorder(nodes[x].right);}void postorder(int x) {	if (nodes[x].left >0) postorder(nodes[x].left);	if (nodes[x].right >0) postorder(nodes[x].right);	cout << char(x + 'A');}int main() {	int n;	cin >> n;		while (n--) {		char node,leftchild, rightchild;		cin >> node >> leftchild >> rightchild;		node -= 'A';		leftchild -= 'A';		rightchild -= 'A';		nodes[node].left = leftchild;		nodes[node].right = rightchild;	}	preorder(0);	printf("\n");	inorder(0);	printf("\n");	postorder(0);}