#include <iostream>#include <algorithm>#include <cstdio>using namespace std;int arr[65][65];int n;bool func(int y, int x, int tmp) {	bool one = false;	bool zero = false;	for (int i = y; i < y+tmp; i++) {		for (int j = x; j < x + tmp; j++) {			if (arr[i][j] == 1) {				one = true;			}			else if (arr[i][j] == 0) {				zero = true;			}			if (one == true && zero == true) return true;		}	}	if (one == true && zero == true) return true; //하나라도틀릴떄	else return false; //다 똑같을때}void recur(int y,int x, int tmp) {	if (!func(y, x, tmp)) {		if (arr[y][x] == 1) {			cout << '1';		}		else {			cout << '0';		}	}	else {		cout << '(';		//왼위,오위,왼아,오아		recur(y, x, tmp / 2);		recur(y, x + tmp/2, tmp / 2);		recur(y + tmp/2, x, tmp / 2);		recur(y + tmp/2, x + tmp/2, tmp / 2);		cout << ')';	}}int main() {	cin >> n;	for (int i = 0; i < n; i++) {		for (int j = 0; j < n; j++) {			scanf("%1d", &arr[i][j]);		}	}	recur(0, 0, n);}