#include <iostream>#include <cstdio>#include <queue>using namespace std;int arr[100][100];bool check[101][101];int n,m;int dx[4] = { -1,0,1,0 };int dy[4] = { 0,-1,0,1 };void bfs() {	queue<pair<int,int>> q;	q.push(make_pair(0, 0));	pair<int, int> p;	check[0][0] = true;	while (!q.empty()) {		p = q.front();		q.pop();		for (int i = 0; i < 4; i++) {			int nx = p.second + dx[i]; //오른쪽 왼쪽 [first][second]			int ny = p.first + dy[i]; //위 아래			if (0 <= nx&&nx < m && 0 <= ny&&ny < n&& arr[ny][nx]==1 && check[ny][nx]==false ) {				q.push(make_pair(ny, nx));				arr[ny][nx] = arr[p.first][p.second] + 1;				check[ny][nx] = true;			}		}	}}int main() {	cin >> n >> m;	for (int i = 0; i < n; i++) {		for (int j = 0; j < m; j++) {			scanf("%1d", &arr[i][j]);		}	}	bfs();	cout << arr[n-1][m-1]<<endl;}