#include <iostream>#include <algorithm>#include <vector>#include <functional>using namespace std;int main() {	int n;	vector<int> vec;	int tmp;	cin >> n;	for (int i = 0; i < n; i++) {		cin >> tmp;		vec.push_back(tmp);	}	sort(vec.begin(), vec.end(),greater<int>());		int comp = 0;	int j = 1;	for (int i = 0; i < n; i++) {		if (comp < vec[i] * j)comp = vec[i] * j;		j++;	}	cout << comp;}