#include <iostream>using namespace std;int main() {	int N;	cin >> N;	for (int i = 1; i <= N; i++) {		for (int j = 1; j <= i; j++) { cout << "*"; }		for (int j = 1; j <= 2 * N - 2 * i; j++) { cout << " "; }		for (int j = 1; j <= i; j++) { cout << "*"; }		cout << endl;	}	for (int i = 2; i <= N; i++) {		for (int j = N; j >= i; j--) { cout << "*"; }		for (int j = 1; j <= (i - 1) * 2;j++) { cout << " "; }		for (int j = N; j >= i; j--) { cout << "*"; }		cout << endl;	}}//1 8 1//2 6 2//3 4 3//4 2 4//5 0 5