#include <iostream>using namespace std;int main() {	int N;	cin >> N;	for (int i = 0; i < N; i++) {		for (int j = 0; j < i; j++) { cout << " "; }		for (int j = 0; j < (N - i)*2-1; j++) { cout << "*"; }		cout << endl;	}	for (int i = 1; i < N; i++) {		for (int j = N-i-1; j > 0; j--) { cout << " "; }		for (int j = 0; j < 2*i+1; j++) { cout << "*"; }		cout << endl;	}}//0 9 //1 7 //2 5 //3 3 //4 1 //3 3//2 5//1 7//0 9