#include <iostream>using namespace std;int arr[100][100];int main() {	int n;	int a, b;	scanf("%d", &n);	while (n--) {		scanf("%d %d", &a, &b);		for (int i = 0; i < 10; i++) {			for (int j = 0; j < 10; j++) {				arr[a + i][b + j] = 1;			}		}	}	int cnt = 0;	for (int i = 0; i <= 100; i++) {		for (int j = 0; j <= 100; j++) {			if (arr[i][j]) cnt++;		}	}	cout << cnt;}