#include <iostream>#include <algorithm>using namespace std;int point[301];int now[301];int main() {	int n;	cin >> n;	for (int i = 0; i < n; i++) {		scanf("%d", &point[i]);	}	now[0] = point[0];	now[1] = point[0]+point[1];	now[2] = max(point[0] + point[2], point[1] + point[2]);	for (int i = 3; i < n; i++) {		now[i] = max(now[i - 2] + point[i], point[i]+point[i-1]+ now[i-3]);	}	cout << now[n - 1];}