#include <iostream>#include <vector>using namespace std;int check[101];int cnt = 0;vector<int> vec[101];void dfs(int start) {	check[start] = true;	cnt++;	for (int i = 0; i < vec[start].size(); i++) {		int next = vec[start][i];		if (!check[next]) {			dfs(next);		}	}}int main() {	int n;	int e;	cin >> n >> e;	int a, b;	for (int i = 0; i < e; i++) {		cin >> a >> b;		vec[a].push_back(b);		vec[b].push_back(a);	}	dfs(1);	cout << cnt-1;}