#include <iostream>#include <vector>#include <algorithm>using namespace std;int n;int arr[26][26];int dx[4] = { 1,-1,0,0 }; //오른쪽,왼쪽int dy[4] = { 0,0,1,-1 };//위,아래int check[100][100];int cnt = 0;int num[1000] = { 0, };void dfs(int x, int y) {	check[x][y] = true;	num[cnt]++;	for (int i = 0; i < 4; i++) {		int nx = x + dx[i];		int ny = y + dy[i];		if (0 <= nx && nx < n && 0 <= ny && ny < n) {			if (!check[nx][ny] && arr[nx][ny] == 1) {				dfs(nx, ny);			}		}	}}int main() {	cin >> n;	for (int i = 0; i < n; i++) {		for (int j = 0; j < n; j++) {			scanf("%1d", &arr[i][j]);		}	}	for (int i = 0; i < n; i++) {		for (int j = 0; j < n; j++) {			if (!check[i][j] && arr[i][j] == 1) {				dfs(i, j);				cnt++;			}		}	}	sort(num, num + cnt);	printf("%d\n", cnt);	for (int i = 0; i < cnt; i++) {		printf("%d\n", num[i]);	}}