#include <iostream>#include <vector>#include <algorithm>#include <functional>using namespace std;int main() {	int n, m;	cin >> n >> m;	vector<int> vec(n);	int tmp;	for (int i = 0; i < n; i++) {		cin >> vec[i];	}	vector<int> sum;	int i, j, k;	for (i = 0; i < n; i++) {		for (j = i+1; j < n; j++) {			for (k = j+1; k < n;  k++) {				sum.push_back(vec[i] + vec[j] + vec[k]);							}		}	}	sort(sum.begin(), sum.end(),greater<int>());	for (int i = 0; i < sum.size(); i++) {		if (sum[i] <= m) {			cout << sum[i];			break;		}	}}