#include <iostream>#include <algorithm>#include <vector>using namespace std;vector<long long> vec;int main() {	long long n, m; //나무의 수 n, 가져갈 목표길이 m	cin >> n >> m;	long long tmp;	long long low = 0, high = 0;	for (int i = 0; i < n; i++) {		cin >> tmp;		if (tmp > high)high = tmp;		vec.push_back(tmp);	}	long long mid = 0; //이분탐색 중간점	long long ans; //가져갈 나무(m과 비교)	long long result = 0; //절단기 설정 높이의 최댓값	while (low <= high) {		ans = 0;		mid = (low + high) / 2;		for (int i = 0; i < n; i++) {			if (vec[i] >= mid) {				ans += vec[i] - mid;			}		}		if (ans >= m) {			if (result < mid) {				result = mid;			}			low = mid + 1;		}		else {			high = mid - 1;		}	}	cout << result;}