#include <iostream>#include <algorithm>using namespace std;int main() {	double a, b, c;	double i, j, k;	double r1, r2, r3;	cin >> a >> b >> c;	cin >> i >> j >> k;	r1 = a / i;	r2 = b / j;	r3 = c / k;	double mn = min(r1, min(r2, r3));	a = a - i*mn;	b = b - j*mn;	c = c - k*mn;	if (a < 1) a = 0;	if (b < 1) b = 0;	if (c < 1) c = 0;	printf("%lf %lf %lf", a, b, c);}