#include <iostream>#include <algorithm>#include <math.h>#include <string>#include <vector>using namespace std;int main() {	while (1) {		int a, b;		char t;		cin >> a >> t >> b;		if (a == 0 && t == 'W'&&b == 0) {			break;		}		if (t == 'W') {			if (a - b < -200) {				cout << "Not allowed"<<endl;			}			else {				cout << a - b << endl;			}		}		else if (t == 'D') {			cout << a + b << endl;		}	}}