#include <iostream>#include <algorithm>#include <math.h>using namespace std;int main() {	int a, b, c;	while (cin>>a>>b>>c&&(a&&b&&c)) {		int t = max(a, max(b, c));		if (a == t) {			if (pow(a, 2) == pow(b, 2) + pow(c, 2)) {				cout << "right"<<endl;			}			else cout << "wrong"<<endl;		}		else if (b == t) {			if (pow(b, 2) == pow(a, 2) + pow(c, 2)) {				cout << "right" << endl;			}			else cout << "wrong" << endl;		}		else if (c == t) {			if (pow(c, 2) == pow(b, 2) + pow(a, 2)) {				cout << "right" << endl;			}			else cout << "wrong" << endl;		}	}}