#include <iostream>#include <vector>using namespace std;vector<int> vec;int cnt;int n;int main() {	while (1) {		vec.clear();		cnt = 0;		while (1) {			cin >> n;			if (n == 0) {				cout << cnt << '\n';				break;			}			if (n == -1) {				break;			}			for (int i = 0; i < vec.size(); i++) {				if (n * 2 == vec[i] || n == vec[i]*2) cnt++;			}			vec.push_back(n);		}		if (n == -1) {			break;		}	}}