#include <iostream>#include <vector>#include <algorithm>using namespace std;int main() {	int n; //토핑종류수	int a; //도우가격	int b; //토핑가격	int c; //도우열량	vector<int>d; //토핑열량들(n개)	int tmp;	cin >> n;	cin >> a >> b;	cin >> c;	for (int i = 0; i < n; i++) {		cin >> tmp;		d.push_back(tmp);	}	sort(d.begin(), d.end(),greater<int>());	int Won = a;	int Yeol = c;			for (int i = 0; i < n; i++) {		if (d[i]/b > Yeol/Won) { //토핑 1원당열량이 더크면			Won += b; //토핑 가격 더해줌			Yeol += d[i];//토핑 열량을 더해줌		}	}	cout << Yeol / Won;//	c+d[?]*b   /  a+b*?	}