#include <iostream>#include <string>using namespace std;int main() {	//char a[16];	//cin.getline(a, 16);	string b;	getline(cin, b);	int sum = 0;	for (int i = 0; i < b.length(); i++) {		if ('A' <= b[i] && 'C' >= b[i]) {			sum += 3;		}		else if ('D' <= b[i] && 'F' >= b[i]) {			sum += 4;		}		else if ('G' <= b[i] && 'I' >= b[i]) {			sum += 5;		}		else if ('J' <= b[i] && 'L' >= b[i]) {			sum += 6;		}		else if ('M' <= b[i] && 'O' >= b[i]) {			sum += 7;		}		else if ('P' <= b[i] && 'S' >= b[i]) {			sum += 8;		}		else if ('T' <= b[i] && 'V' >= b[i]) {			sum += 9;		}		else if ('W' <= b[i] && 'Z' >= b[i]) {			sum += 10;		}	}	cout << sum << endl;}