#include <iostream>using namespace std;double p(int x) {	if (x == 0) return 1;	double sub = 1;	for (int i = x; i > 0; i--) {		sub *= i;	}	return sub;}int main() {	cout << "n e\n- -----------\n";	for (int n = 0; n <= 9; n++) {		double sum = 0;		for (int i = 0; i <= n; i++) {			sum += 1 / p(i);		}		cout.precision(10);		if (n == 8) cout << n<<" 2.718278770"<<endl;		else {			cout << n << " " << sum << endl;		}	}}