#include <iostream>#include <cstring>using namespace std;int main() {	int testcase;	cin >> testcase;	int *score = new int[testcase];	for (int i = 0; i <= testcase; i++) {		char input[80];		cin.getline(input,80);		int len = strlen(input);		int sum = 0, count = 0;		for (int j = 0; j < len; j++) {			if (input[j] == 'X') {				count = 0;			}			else {				++count;				sum += count;		//		cout << sum << endl;			}		}		score[i] = sum;	}	for (int k = 1; k <= testcase; k++) cout << score[k] << endl;}