#include <iostream>#include <string.h>using namespace std;long long dp[102];long long func(long long x) {	dp[1] = 1;	dp[2] = 1;	dp[3] = 1;	if (x == 1)return dp[1];	if (x == 2) return dp[2];	if (x == 3) return dp[3];	if (dp[x] != 0) {		return dp[x];	}	else {		return dp[x] = func(x - 2) + func(x - 3);	}}int main() {	int t;	cin >> t;	while (t--) {		long long n;		cin >> n;		memset(dp, 0, sizeof(dp));		cout << func(n) << endl;	}}