#include <iostream>#include <algorithm>#include <vector>using namespace std;int main(){	int n,m,t;	scanf("%d",&n);	vector<int>vec(n);	for(int i=0;i<n;i++){		scanf("%d",&vec[i]);	}	sort(vec.begin(),vec.end());	scanf("%d",&m);	for(int i=0;i<m;i++){		scanf("%d",&t);		printf("%d ",binary_search(vec.begin(),vec.end(),t));	}}