#include <iostream>#include <queue>#include <string>using namespace std;queue<int> a;string b;int main() {	int N, num;	cin >> N;	while (N--) {		cin >> b;		if (b == "push") {			cin >> num;			a.push(num);		}		else if (b == "pop") {			if (a.empty() == true) {				cout << -1 << endl;			}			else {				cout << a.front() << endl;				a.pop();			}		}		else if (b == "size") {			cout << a.size() << endl;		}		else if (b == "empty") {			if (a.empty() == true) cout << 1 << endl;			else cout << 0 << endl;		}		else if (b == "front") {			if (a.empty() == true) {				cout << -1 << endl;			}			else cout << a.front() << endl;		}		else if (b == "back") {			if (a.empty() == true) {				cout << -1 << endl;			}			else cout << a.back() << endl;		}	}}