#include <iostream>#include <queue>using namespace std;int main() {	queue<int> q;	int n, m,x;	cin >> n >> m;	int a[1001];	for (int i = 1; i <= n; i++) {		q.push(i);	}	for (int j = 0; j < n; j++) {		for (int i = 0; i < m-1; i++) {			x = q.front();			q.pop();			q.push(x);		}		a[j] = q.front();		q.pop();	}	cout << "<";	for (int i = 0; i < n-1; i++) {		cout << a[i]<<", ";	}	cout << a[n-1];	cout << ">";}