#include <iostream>#include <algorithm>#include <vector>#include <cmath>using namespace std;int t[16];int p[16];int n;int result=0;void dfs(int day, int sum_pay) {	if (day >= n) {		result = max(result, sum_pay);		return;	}	if (day + t[day] <= n) {		dfs(day + t[day], sum_pay + p[day]);	}	if (day + 1 <= n) {		dfs(day + 1, sum_pay);	}}int main() {	cin >> n;	for (int i = 0; i < n; i++) {		cin >> t[i] >> p[i];	}	dfs(0, 0);	cout << result;}