#include <iostream>#include <algorithm>using namespace std;int n, m;int arr[8];int arr2[8];bool visit[8];void func(int start, int depth) {	if (depth == m) {		for (int i = 0; i < m; i++) {			cout << arr2[i] << " ";		}		cout << '\n';		return;	}	for (int i = start; i < n; i++) {		if (!visit[i]) {			arr2[depth] = arr[i];			visit[i] = true;			func(i + 1, depth + 1);			visit[i] = false;		}	}}int main() {	cin >> n >> m;	for (int i = 0; i < n; i++) {		cin >> arr[i];	}	sort(arr, arr + n);	func(0, 0);}