#include <iostream>#include <string>using namespace std;int main() {	string str;	getline(cin, str);	int check[4] = { 0, };	for (int i = 0; i < str.length(); i++) {		if (str[i] == 'U') {			check[0] = 1;		}		if (check[0] == 1 && str[i] == 'C') {			check[1] = 1;		}		if (check[0] == 1 && check[1] == 1 && str[i] == 'P') {			check[2] = 1;		}		if (check[0] == 1 && check[1] == 1 && check[2] == 1 && str[i] == 'C') {			check[3] = 1;		}	}	if (check[3]) {		cout << "I love UCPC";	}	else {		cout << "I hate UCPC";	}}